#include <cstdio>#include <string>#include <iostream>#include <vector>#include <algorithm>#include <utility>using namespace std;vector< pair<int, string> > teman;int dp(int mulai, int tujuan, int a, int b){	//printf("%d %d %d %d\n", mulai, tujuan, a, b);	int ans = 0;	if (tujuan < mulai){		if ((mulai/2)>=tujuan){			//bisa dibagi 2			if ((a*(mulai-(mulai/2))) > b){			//efisien pake b				ans = b + dp(mulai/2, tujuan, a, b);			}			else{									//efisien pake a				ans = (a*(mulai-(mulai/2))) + dp(mulai/2, tujuan, a, b);			}		}		else{			ans = a * (mulai - tujuan);		}	}	return ans;}int main(){	int testcase;	int mulai, tujuan, n;	int a, b;	int temp;	char aaa[100];	string tempString;	scanf("%d", &testcase);	getchar();	for (int i=1; i<=testcase; i++){		teman.clear();		scanf("%d %d %d", &n, &mulai, &tujuan);		getchar();		for (int j=0; j<n; j++){			scanf("%[^:]s", aaa);			tempString = aaa;			getchar();			getchar();			cin >> a;			cin >> b;			getchar();			temp = dp(mulai, tujuan, a, b);			teman.push_back(make_pair(temp, tempString));		}		sort(teman.begin(), teman.end());		for (int j=0; j<n; j++){			cout << teman[j].second << ": " << teman[j].first;		}		if (i>0)			cout << endl;	}	return 0;}